import React from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetail.css';

const ProductDetail = () => {
  const { id } = useParams(); // Lấy ID từ URL (có thể không cần nếu hardcode tất cả)

  // Danh sách sản phẩm hardcode
  const products = [
    {
      _id: "1",
      name: "Product 1",
      price: 100,
      description: "A high-quality electronic product with great features.",
      category: "Electronics",
      imageUrl: "/images/product1.jpg",
    },
    {
      _id: "2",
      name: "Product 2",
      price: 150,
      description: "This is another amazing product that you will love.",
      category: "Electronics",
      imageUrl: "/images/product2.jpg",
    },
    {
      _id: "3",
      name: "Product 3",
      price: 200,
      description: "The best product in this category with premium quality.",
      category: "Electronics",
      imageUrl: "/images/product3.jpg",
    }
  ];

  // Tìm sản phẩm theo ID (nếu không tìm thấy thì chọn sản phẩm đầu tiên)
  const product = products.find((p) => p._id === id) || products[0];

  return (
    <div className="product-detail">
      <img src={product.imageUrl} alt={product.name} className="product-image" />
      <h2>{product.name}</h2>
      <p className="price">${product.price}</p>
      <p className="description">{product.description}</p>
    </div>
  );
};

export default ProductDetail;
