import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './ProductList.css'; // Đảm bảo rằng đường dẫn đúng với vị trí của tệp CSS

const ProductList = () => {
  const [products, setProducts] = useState([]);  // State lưu trữ danh sách sản phẩm
  const [loading, setLoading] = useState(true);   // State kiểm tra trạng thái loading
  const [cart, setCart] = useState([]);           // State lưu trữ giỏ hàng

  // Hàm gọi API để lấy danh sách sản phẩm
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/products');
        setProducts(response.data);  // Cập nhật state products với dữ liệu trả về
        setLoading(false);           // Cập nhật trạng thái loading
      } catch (error) {
        console.error('Error fetching products', error);
        setLoading(false);  // Cập nhật khi có lỗi
      }
    };

    fetchProducts();  // Gọi API khi component được render
  }, []);  // [] là mảng phụ thuộc, nghĩa là useEffect chỉ chạy 1 lần khi component mount

  // Hàm thêm sản phẩm vào giỏ hàng
  const addToCart = (product) => {
    setCart((prevCart) => {
      const updatedCart = prevCart.find(item => item._id === product._id)
        ? prevCart.map(item => item._id === product._id ? { ...item, quantity: item.quantity + 1 } : item)
        : [...prevCart, { ...product, quantity: 1 }];

      // Lưu giỏ hàng vào localStorage
      localStorage.setItem('cart', JSON.stringify(updatedCart));
      return updatedCart;
    });
  };

  // Hàm tính tổng số sản phẩm trong giỏ hàng
  const getTotalItems = () => {
    return cart.reduce((total, product) => total + product.quantity, 0);
  };

  if (loading) {
    return <div>Loading...</div>;  // Hiển thị khi đang tải dữ liệu
  }

  return (
    <div>
      <h2>Product List</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product._id} className="product-card">
            {/* Hiển thị ảnh sản phẩm */}
            <img 
              src={product.imageUrl} 
              alt={product.name} 
              className="product-image" 
              style={{ width: '200px', height: 'auto' }} 
            />
            <div className="product-info">
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>Price: ${product.price}</p>
              {/* Thêm nút "Add to Cart" */}
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>

      {/* Hiển thị thông tin giỏ hàng */}
      <div className="cart-info">
        <h3>Cart ({getTotalItems()} items)</h3>
        {/* Hiển thị các sản phẩm trong giỏ hàng */}
        <ul>
          {cart.map((product) => (
            <li key={product._id}>
              {product.name} - {product.quantity} x ${product.price}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ProductList;
