import React from 'react';
import { Link } from 'react-router-dom'; // Để sử dụng các đường dẫn trong React

const Header = () => {
  return (
    <header style={headerStyle}>
      <div style={logoStyle}>
        <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>
          <h1>My E-Commerce</h1>
        </Link>
      </div>
      <nav style={navStyle}>
        <ul style={navListStyle}>
          <li style={navItemStyle}><Link to="/" style={linkStyle}>Home</Link></li>
          <li style={navItemStyle}><Link to="/products" style={linkStyle}>Products</Link></li>
          <li style={navItemStyle}><Link to="/cart" style={linkStyle}>Cart</Link></li>
          <li style={navItemStyle}><Link to="/about" style={linkStyle}>About</Link></li>
        </ul>
      </nav>
    </header>
  );
};

// Styling cho Header (inline CSS)
const headerStyle = {
  background: '#333',
  color: '#fff',
  padding: '10px 0',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  paddingLeft: '20px',
  paddingRight: '20px',
};

const logoStyle = {
  fontSize: '24px',
  fontWeight: 'bold',
};

const navStyle = {
  listStyle: 'none',
};

const navListStyle = {
  display: 'flex',
  margin: 0,
  padding: 0,
};

const navItemStyle = {
  marginRight: '20px',
};

const linkStyle = {
  color: 'white',
  textDecoration: 'none',
  fontSize: '18px',
};

export default Header;
