import React from 'react';
import { Route, BrowserRouter as Router, Routes } from 'react-router-dom';
import './App.css';
import CartPage from './components/CartPage';
import FeaturedProducts from './components/FeaturedProducts'; // Import đúng
import Header from './components/Header'; // Import Header
import Login from './components/Login'; // Nhập Login
import Logout from './components/Logout'; // Nhập Logout
import ProductDetail from './components/ProductDetail';
import ProductList from './components/ProductList';

const App = () => {
  return (
    <Router>
      <div>
        <Header /> {/* Thêm thanh Header ở đây */}

        {/* Đảm bảo rằng FeaturedProducts được hiển thị */}
        <FeaturedProducts /> {/* Đảm bảo rằng carousel sản phẩm nổi bật được gọi */}

        <Routes>
          <Route path="/" element={<ProductList />} /> {/* Trang danh sách sản phẩm */}
          <Route path="/cart" element={<CartPage />} /> {/* Trang giỏ hàng */}
          <Route path="/product/:id" element={<ProductDetail />} /> {/* Chi tiết sản phẩm */}
          <Route path="/login" element={<Login />} /> {/* Trang đăng nhập */}
          <Route path="/logout" element={<Logout />} /> {/* Trang đăng xuất */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
