import React from 'react';
import { Route, Routes } from 'react-router-dom'; // Import đúng để dùng Routes thay vì Switch
import './App.css';
import CartPage from './components/CartPage';
import FeaturedProducts from './components/FeaturedProducts';
import Header from './components/Header';
import Login from './components/Login';
import Logout from './components/Logout';
import ProductDetail from './components/ProductDetail';
import ProductList from './components/ProductList';
import Register from './components/Register';
import SearchResults from './components/SearchResults';
import ProductListAdmin from './ProductListAdmin'; // Trang quản trị viên

const App = () => {
  return (
    <div>
      <Header />
      <FeaturedProducts />
      <Routes>
        <Route path="/" element={<ProductList />} />
        <Route path="/product/:id" element={<ProductDetail />} />
        <Route path="/cart" element={<CartPage />} />
        <Route path="/login" element={<Login />} />
        <Route path="/logout" element={<Logout />} />
        <Route path="/register" element={<Register />} /> {/* Đã thêm Register */}
        <Route path="/search/:keyword" element={<SearchResults />} />
        <Route path="/admin/products" element={<ProductListAdmin />} /> {/* Sửa component thành element */}
      </Routes>
    </div>
  );
};

export default App;
