import React from 'react'; // Nh·∫≠p React t·ª´ th∆∞ vi·ªán React
import { Link, useNavigate } from 'react-router-dom'; // Nh·∫≠p useNavigate t·ª´ react-router-dom

const Header = () => {
  const navigate = useNavigate(); // Kh·ªüi t·∫°o useNavigate

  // H√†m x·ª≠ l√Ω khi click v√†o m·ªôt m·ª•c
  const handleSearch = () => {
    const searchTerm = document.getElementById("searchInput").value;
    if (searchTerm) {
      navigate(`/search/${searchTerm}`); // ƒêi·ªÅu h∆∞·ªõng t·ªõi trang t√¨m ki·∫øm v·ªõi t·ª´ kh√≥a
    }
  };

  return (
    <header style={headerStyle}>
      <div style={logoContainerStyle}>
        <Link to="/" style={logoStyle}>
          <h1>BansToys</h1>
        </Link>
      </div>
      <nav style={navStyle}>
        <ul style={navListStyle}>
          <li style={navItemStyle}><Link to="/" style={linkStyle}>Home</Link></li>
          <li style={navItemStyle}><Link to="/products" style={linkStyle}>Product List</Link></li>
          <li style={navItemStyle}><Link to="/cart" style={linkStyle}>üõí Cart</Link></li>
          <li style={navItemStyle}><Link to="/about" style={linkStyle}>About</Link></li>
        </ul>
      </nav>
      <div style={searchContainerStyle}>
        <input 
          id="searchInput"
          type="text" 
          placeholder="Search..." 
          style={searchInputStyle} 
        />
        <button onClick={handleSearch} style={searchButtonStyle}>Search</button>
      </div>
    </header>
  );
};

// Styling cho Header (inline CSS)
const headerStyle = {
  backgroundColor: '#696969',
  color: '#fff',
  padding: '20px 40px',
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
  height: '60px',
  width: '100%',
};

const logoContainerStyle = {
  flex: 1,
};

const logoStyle = {
  fontSize: '28px',
  fontWeight: 'bold',
  color: '#fff',
  textDecoration: 'none',
};

const navStyle = {
  flex: 2,
};

const navListStyle = {
  display: 'flex',
  listStyle: 'none',
  margin: 0,
  padding: 0,
  justifyContent: 'center',
};

const navItemStyle = {
  margin: '0 20px',
};

const linkStyle = {
  color: 'white',
  textDecoration: 'none',
  fontSize: '18px',
  transition: 'color 0.3s ease',
};

const searchContainerStyle = {
  flex: 1,
  display: 'flex',
  justifyContent: 'flex-end',
};

const searchInputStyle = {
  padding: '10px',
  borderRadius: '5px',
  border: 'none',
  width: '250px',
  fontSize: '16px',
};

const searchButtonStyle = {
  padding: '10px',
  marginLeft: '10px',
  backgroundColor: '#3498db',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

export default Header;
